exit } }
# Method to resolve path. # Resolve the local path to a full path but do not resolve the network path. Function ResolvePath($path) { $resolvedPath = $path
if(!$path.StartsWith('\\')) { # Resolve local path $resolvedPath = resolve-path($path) }
CheckAndLogError("ResolvePath($path)") return $resolvedPath }
$Error.Clear()
$momAPI = new-object -comObject MOM.ScriptAPI
LogInfo("Executing PowerShell Script")
# ------------------------------------------------------------------------------------------------- # End of Common.ps1 # -------------------------------------------------------------------------------------------------
$StatePropertyBag = $momAPI.CreateTypedPropertyBag($PROPERTY_TYPE_STATE) if ($StatePropertyBag -eq $null) { LogError("unable to create state property bag") Exit }
try { $TxResult = Invoke-Command -ScriptBlock ([scriptblock]::Create($FunctionScript)) } catch [System.IO.IOException] { $StatePropertyBag.AddValue('State', "Error") $StatePropertyBag.AddValue('Message', $_.Exception.Message) $StatePropertyBag Exit } catch { $StatePropertyBag.AddValue('State', "Error") $StatePropertyBag.AddValue('Message', $_.Exception.Message) $StatePropertyBag Exit }
if ($TxResult -eq $null) { $StatePropertyBag.AddValue('State', "Error") $StatePropertyBag.AddValue('Message', "$FunctionName unable to retrieve result") $StatePropertyBag
LogError("$FunctionName unable to retrieve result") Exit }
$StatePropertyBag.AddValue('State', "Success") $StatePropertyBag CheckAndLogError("Returned StatePropertyBag")
$ObjectName = "ManageabilityTest" $CounterName = "Latency" $InstanceName = $FunctionName $Value = $TxResult LogInfo("$FunctionName result is $TxResult")
$PerformancePropertyBag = $momAPI.CreateTypedPropertyBag($PROPERTY_TYPE_PERFORMANCE) if ($PerformancePropertyBag -eq $null) { LogError("unable to create performance property bag") Exit }
$PerformancePropertyBag.AddValue('ObjectName', $ObjectName) $PerformancePropertyBag.AddValue('CounterName', $CounterName) $PerformancePropertyBag.AddValue('InstanceName', $InstanceName) $PerformancePropertyBag.AddValue('Value', $Value) $PerformancePropertyBag CheckAndLogError("Returned PerformancePropertyBag")

WriteAction.ScriptBody:
#RuleId - Alert for RuleId to be closed
param($RuleId,$MGName)

$errorActionPreference = 'Stop'

$momAPI = New-Object -ComObject "MOM.ScriptAPI"

$errEvent = [System.Diagnostics.EventLogEntryType]::Error;
$infoEvent = [System.Diagnostics.EventLogEntryType]::Information;

#Alert Resolution State
$resolutionStateClose = 255;

$script:traceMsg = "";

#Adds tracemessage to $Script
Function AddTraceMessage 
{
    param($message)
    $timeStamp = (get-date -format "HH:mm:ss:fff");
    $script:traceMsg = $script:traceMsg + "`n[" + $timeStamp  + "] " + $message;
}


Try
{
    $error.Clear();

                AddTraceMessage -message "Microsoft Azuew Metric Alert Rule CloseAlert.ps1 started."    

    if([string]::IsNullOrEmpty($RuleId))
                {
                                Throw [System.NullReferenceException] "Null or empty FaultId parameter to the script.";
                }

    #Import-Module OperationsManager
                $omModule = get-module -name "OperationsManager"
                if ($omModule -eq $null)
                {
                                $SCOMPowerShellKey = "HKLM:\SOFTWARE\Microsoft\System Center Operations Manager\12\Setup\Powershell\V2"
                                $SCOMModulePath = Join-Path (Get-ItemProperty $SCOMPowerShellKey).InstallDirectory "OperationsManager" 
                                Import-module $SCOMModulePath | Out-Null
                }

    AddTraceMessage -message "Searching OM Alert with these patterns: `n $RuleId"

	$ManagementGroupSubKeyPath = "\SOFTWARE\Microsoft\Microsoft Operations Manager\3.0\Server Management Groups\" + $MGName + "\Parent Health Services\0"
	if (-not ([string]::IsNullOrWhiteSpace(($MGName))) -And (Test-Path "HKLM:$ManagementGroupSubKeyPath"))
	{
		$ComputerName = (Get-ItemProperty "HKLM:$ManagementGroupSubKeyPath").NetworkName
		New-SCOMManagementGroupConnection -ComputerName $ComputerName
	}
	else
	{
		$ComputerName = "localhost"
		New-SCOMManagementGroupConnection -ComputerName $ComputerName
	}  

    $Criteria = "MonitoringObjectName='" + $RuleId + "'"

    $alert = Get-SCOMAlert -Criteria \"$Criteria\"

    if($alert -ne $null)
                {
                                AddTraceMessage -message ("Alert found. Name = " + $alert[0].Name)
                                Set-SCOMAlert -Alert $alert -ResolutionState $resolutionStateClose
                                AddTraceMessage -message "Alert is closed."
                }
                else
                {
                                AddTraceMessage -message "Could not find OM Alert with given pattern."
                }                              

                AddTraceMessage -message "Microsoft Azuew Metric Alert Rule CloseAlert.ps1 completed."

}
Catch
{
                AddTraceMessage -message $_.Exception.Message         
}
Finally
{   
                 if ($error -ne $null -and $error.Count -gt 0 -and $error[0] -ne $null)
                {
                                $momAPI.LogScriptEvent("CloseAlert.ps1", 6420, 1, $error[0].ToString());                             
                }

                if ($script:traceMsg -ne $null)
                {
                                $momAPI.LogScriptEvent("CloseAlert.ps1", 6421, 0, $script:traceMsg);                        
                }
}

