window.onload = function() {
	var data = [];
	var alertsCache = [];
	var alertsGroupedCache = [];
	var excludeMonitors = ['Performance', 'Availability', 'Configuration', 'Security', 'Entity Health'];
	var selectedInstanceId = '';
	var classIdSelected = '';
	var drilledDown = false;
    
	var requestHeaders = {
		Accept: 'q=0.8;application/json;q=0.9'
	};

	function InitializeCSRFToken() {
		var documentcookies = document.cookie.split('; ');
		var result = {};
		for (var i = 0; i < documentcookies.length; i++) {
			var cur = documentcookies[i].split('=');
			result[cur[0]] = cur[1];
		}
		if (result["SCOM-CSRF-TOKEN"] && result["SCOM-CSRF-TOKEN"] != null) {
			requestHeaders["SCOM-CSRF-TOKEN"] = decodeURIComponent(result["SCOM-CSRF-TOKEN"]);
		}
	}

	InitializeCSRFToken();

	
	function getAlertsDataCache(){
		return $.ajax({
			url: "/operationsmanager/data/alert/",
			type: "POST",
			headers: requestHeaders,
			data: {
				'criteria': "ResolutionState = '0'",
				'displayColumns': ['severity', 'name', 'age', 'repeatcount', 'monitoringobjectdisplayname', 'classid', 'monitoringobjectid']
			},
			error: function (result, textStatus, errorThrown) {
				console.log('loading alerts data error: ' + result.responseJSON.errorMessage);
			},
			success: function (result) {
				result.rows.forEach(function(entry){
					alertsCache.push(entry);
				});
			}
		});
	}

	getAlertsDataCache();

	function processAlerts(dataItem, dataItemIndex)	{
		var alerts = [];

		dataItem.instances.forEach(function(instance, instanceIndex){
			var instanceAlerts = alertsCache.filter( entry => entry.monitoringobjectid == instance.id);
			alerts = alerts.concat(instanceAlerts);
			alertsGroupedCache[instance.id] = instanceAlerts;
		});
		renderAlerts(alerts);

		function renderAlerts(rows) 
		{
			if(rows.length == 0) 
				return;
			//var totalCount = rows.length;
			var criticalCounter = 0;
			var informationCounter = 0;
			var warningCounter = 0;

			rows.forEach(function(row)
			{
					switch(row.severity)
					{
						case "Error":
							criticalCounter++; break;
						case "Information":
							informationCounter++; break;
						case "Warning":
							warningCounter++; break;
					}
			});

			renderChart(criticalCounter, warningCounter, informationCounter);

			//console.log(`done renderAlerts: ${dataItem.classId}, count: ${rows.length}`);
		}
		// console.log(`done renderAlerts: ${dataItem.classId}, count: ${alerts.length}`);

		function renderChart(criticalCounter, warningCounter, informationCounter) 
		{
			if(criticalCounter > 0)
			{
				stateCount = criticalCounter;
				stateMessage = 'CRITICAL';
				color = 'critical-alert';
			}
			else if(warningCounter > 0)
			{
				stateCount = warningCounter;
				stateMessage = 'WARNING';
				color = 'warning-alert';
			}
			else if(informationCounter > 0)
			{
				stateCount = informationCounter;
				stateMessage = 'INFORMATION';
				color = 'ok-group';
			}
			else
			{
				stateCount = 0;
				stateMessage = 'NO ALERTS';
				color = 'no-alert';
				// console.log(`done renderChart; no alerts`);
				return;
			}
			//stateCount = stateCount + ' of ' + totalCount;
			
			var group = $(`div[classId|='${dataItem.classId}']`);
			var spans = $(group).find('.alerts span');
			spans.eq(1).text(stateCount);
			spans.eq(2).text(stateMessage);
			var tile = $(group).find('.alerts');
			tile.removeClass('no-alert');
			tile.addClass(color);

			// console.log(`done renderChart; count: ${stateCount}`);
		}
	}

	
	function getCachedAlerts(instanceId){
		var result = alertsGroupedCache[instanceId];
		$('.alert').remove(); // clean old records
		if(result)
			result.forEach(function(entry){
				$('#alerts-table').append('<tr style="line-height:20px" class="alert"><td>' + entry.name + '</td><td>' + entry.age + '</td><td>' + entry.repeatcount + '</td></tr>');
			});
		else
			console.log(`getCachedAlerts: alertsGroupedCache doesn't have key ${instanceId}`);
	}

	function getChildCachedAlerts(instances){
		var alerts = [];
		if(alertsGroupedCache.includes(instances[0].id))
			return;
		instances.forEach(function(instance, instanceIndex){
			var instanceAlerts = alertsCache.filter( entry => entry.monitoringobjectid == instance.id);
			alerts = alerts.concat(instanceAlerts);
			alertsGroupedCache[instance.id] = instanceAlerts;
		});
	}

