	function getMonitors(instance){
		return $.ajax({
			url: "/operationsmanager/data/object/monitors/",
			type: "POST",
			headers: requestHeaders,
			data: {
				"id": instance.id,
			},
			error: function (result, textStatus, errorThrown) {
				//alert('error!');
			},
			success: function (result) {
				result.rows.forEach(function(entry){
					var clas = 'no-states';
					var state = 'NOT MONITORED'
					if(entry.state.includes('omplete'))
					{
						clas = 'ok-group';
						state = 'HEALTHY';
					}
					else if(entry.state.includes('ritical'))
					{
						clas = 'critical-group';
						state = 'CRITICAL';
					}
					else if(entry.state.includes('arning'))
					{
						clas = 'warning-group';
						state = 'WARNING';
					}
					else if(entry.state.includes('nknown'))
					{
						clas = 'no-states';
						state = 'UNKNOWN';
					}
					
					if(excludeMonitors.includes(entry.displayname))
						return;
					var name = entry.displayname.length > 50
						? entry.displayname.substring(0, 50) + '...'
						: entry.displayname;
					var date = new Date(entry.laststatechange);
					var seconds = date.getSeconds();
					var secondsString = seconds > 9 ? seconds + '' : '0' + seconds;
					var displayDate = (date.getMonth() + 1) + '/' + date.getDate() + '/' + date.getFullYear() + ' ' + date.getHours() + ':' + date.getMinutes() + ':' + secondsString;
					$('#monitors').append('<div class="monitor ' + clas + '" name="' + instance.id + '"><span class="name">' + name + '</span><span class="state">' + state + '</span><span class="date">' + displayDate + '</span></div>');
				});
			}
		});
	}
	
	function onGroupClick(group) {
		$('#monitor-group').html(group.html());
		$('#monitor-group .tile').css('width', 174);
		$('#monitor-group .group-header').remove();
		var classId = group.attr('classId');
		if(classId == null || classId == ''){
			classId = classIdSelected;
		}
		var name = group.find('.group-header').text();
		$('#group-name span').text(' | ' + name);
		$('.group').hide();
		$('#group-name, #properties-table, #alerts-table, #alerts-caption, .state-header').show();

		var instances = [];
		var dataItem = data.find(function(item){
			return item.classId == classId;
		});
		if(group.hasClass('group')){
			$('#monitor-group').show();
			classIdSelected = classId;
			instances = dataItem.instances;
			drilledDown = false;
			//getChildInstancesOf(dataItem);
		}else{
			var instance = dataItem.instances.find(function(i){
				return i.id == selectedInstanceId;
			});
			instances = instance.childs;
			drilledDown = true;
			$('#monitor-group').hide();
			getChildCachedAlerts(instances);
		}
		var hasCritical = false;
		var hasWarning = false;
		var hasUnknown = false;
		var hasHealthy = false;
		$('.instance').remove();
		instances.forEach(function(instance){
			var name = instance.displayname !== undefined ? instance.displayname : instance.displayName;
			var div = '<div class="instance" name="' + instance.id + '">' + name + '</div>';
			if (instance.healthstate == 'critical' || ( instance.healthIconUrl !== undefined && instance.healthIconUrl.includes('Critical')))
			{
				$('.critical-instance').after(div);
				hasCritical = true;
			}
			else if(instance.healthstate == 'warning' || ( instance.healthIconUrl !== undefined && instance.healthIconUrl.includes('Warning')))
			{
				$('.warning-instance').after(div);
				hasWarning = true;
			}
			else if(instance.healthstate == 'unknown' || ( instance.healthIconUrl !== undefined && instance.healthIconUrl.includes('Unknown')))
			{
				$('.unknown-instance').after(div);
				hasUnknown = true;
			}
			else if(instance.healthstate == 'healthy' || ( instance.healthIconUrl !== undefined && instance.healthIconUrl.includes('Healthy')))
			{
				$('.ok-instance').after(div);
				hasHealthy = true;
			}
		});
		
		if(!hasCritical)
			$('.critical-instance').hide();
		if(!hasWarning)
			$('.warning-instance').hide();
		if(!hasUnknown)
			$('.unknown-instance').hide();
		if(!hasHealthy)
			$('.ok-instance').hide();
		
		$('.instance').click(function() {
			$('.instance').css('border', '1px solid white');
			$('.instance').css('font-weight', 'normal');
			$(this).css('border', '1px solid LightBlue');
			$(this).css('font-weight', 'bold');
			var id = $(this).attr('name');
			if(!drilledDown)
			{
				selectedInstanceId = id;
				//getCachedAlerts(id);
			}			
			getCachedAlerts(id);
			//getAlerts($(this).text(), null);
			var healthExplorer = $('#group-name a:eq(1)');
			healthExplorer.attr('href', '/MonitoringView/ResultViews/ViewTypeHealthExplorer.aspx?targetId=' + id);
			healthExplorer.show();
			$('#properties-container').empty();
			var dataItem = data.find(function(item){
				return item.classId == classIdSelected;
			});
			var instance = dataItem.instances.find(function(i){
				return i.id == selectedInstanceId;
			});
			if(drilledDown)
				instance = instance.childs.find(function(child){
					return child.id == id;
				});
			instance.properties.forEach(function(property){
				if(property.value != null && property.value != '')
					$('#properties-container').append('<b>' + property.name + '</b><br>' + property.value + '<br><br>');
			});

			$('.monitor').hide();
			$('.monitor[name="' + id + '"]').show();
			$('#monitors-dispatcher, #monitors').show();
		});
		
		$('.instance:first').trigger('click');
	}
	
	$('#group-name a:first').click(function() {
		if(drilledDown){
