	});
	
	function renderState(group) {
		var classId = group.attr('classId');
		var stateCount = '0';
		var stateMessage = 'NO STATES';
		var color = 'no-states';

		if(classId != null && classId)
		return $.ajax({
			url: "/operationsmanager/data/state",
			type: "POST",
			headers: requestHeaders,
			data: {
                "classId": classId,
                "objectIds": [],
                "criteria": null,
                "displayColumns":["displayname", "healthstate"]
            },
			error: function (result, textStatus, errorThrown) {
				console.log('loading state data error: ' + result.responseJSON.errorMessage);
				group.removeAttr('classId');
			},
			success: function (result) {
				var totalCount = result.rows.length;
				var criticalCount = 0;
				var warningCount = 0;
				var unknownCount = 0;
				var healthyCount = 0;
				result.rows.forEach(function(row) {
					if(row.healthstate == 'critical')
						criticalCount++;
					else if(row.healthstate == 'warning')
						warningCount++;
					else if(row.healthstate == 'unknown')
						unknownCount++;
					else if(row.healthstate == 'healthy')
						healthyCount++;
					});
				if(criticalCount > 0)
				{
					stateCount = criticalCount;
					stateMessage = 'CRITICAL';
					color = 'critical-group';
				}
				else if(warningCount > 0)
				{
					stateCount = warningCount;
					stateMessage = 'WARNING';
					color = 'warning-group';
				}
				else if(unknownCount > 0)
				{
					stateCount = unknownCount;
					stateMessage = 'UNKNOWN';
					color = 'unknown-group';
				}
				else
				{
					stateCount = healthyCount;
					stateMessage = 'HEALTHY';
					color = 'ok-group';
				}
				stateCount = stateCount + ' of ' + totalCount;
				var spans = $(group).find('.state span');
				spans.eq(1).text(stateCount);
				spans.eq(2).text(stateMessage);
				var tile = $(group).find('.state');
				tile.removeClass('no-states');
				tile.addClass(color);
				
				data.push({'classId': classId, 'instances': result.rows});
			}
		});
	}
	
	function getInstances(dataItem, dataItemIndex){
		var queries = [];
		dataItem.instances.forEach(function(instance, instanceIndex){
			var query = $.ajax({
				url: '/operationsmanager/data/objectInformation/' + instance.id,
				type: "GET",
				headers: requestHeaders,
				error: function (result, textStatus, errorThrown) {
					console.log('get instance error ' + result.responseJSON.errorMessage);
				},
				success: function (result) {
					data[dataItemIndex].instances[instanceIndex].properties = result.monitoringObjectProperties;
					data[dataItemIndex].instances[instanceIndex].childs = result.relatedObjects;
					queries.push(getMonitors(instance));
				}
			});
			queries.push(query);
		});
		
		$.when.apply(null, queries).done(function(){
				processAlerts(dataItem, dataItemIndex);
				var group = $(`div[classId|='${dataItem.classId}']`);
				if(group.hasClass('no-child'))
					return;
				dataItem.instances.forEach(function(instance, instanceIndex){
					instance.childs.forEach(function(child, childIndex){
						// Ugly check to found if instance or child already was loaded
						var found = false;
						data.forEach(function(innerDataItem, innerDataItemIndex){
							if(found) return;
							innerDataItem.instances.forEach(function(innerInstance){
								if(found) return;
								if(innerInstance.id == child.id && innerInstance.properties){
									data[dataItemIndex].instances[instanceIndex].childs[childIndex].properties = innerInstance.properties;
									found = true;
									return;
								}
								instance.childs.forEach(function(innerChild){
									if(found) return;
									if(innerChild.id == child.id && innerChild.properties){
										data[dataItemIndex].instances[instanceIndex].childs[childIndex].properties = innerChild.properties;
										found = true;
										return;
									}
								});
							});
						});
						if(found) return;
						
						// If not - loading
						var childQuery = $.ajax({
							url: '/operationsmanager/data/objectInformation/' + child.id,
							type: "GET",
							headers: requestHeaders,
							error: function (result, textStatus, errorThrown) {
								console.log('get instance child error ' + result.responseJSON.errorMessage);
							},
							success: function (childResult) {
								data[dataItemIndex].instances[instanceIndex].childs[childIndex].properties = childResult.monitoringObjectProperties;
							}
						});
						queries.push(childQuery);
						queries.push(getMonitors(child));
					});
				});
		});

		// $.when.apply(null, queries).done(
		// 	processAlerts(dataItem, dataItemIndex),
		// 	getChildInstances()
		// );		
		
		return queries;
	}
	
