	var clickHandlerAttached = false;
	function getDataItem(dataItem, dataItemIndex){
		var q = getInstances(dataItem, dataItemIndex);
		$.when.apply(null, q).done(function(){
			if(dataItemIndex + 1 < data.length){
				getDataItem(data[dataItemIndex + 1], dataItemIndex + 1);
			}else if(!clickHandlerAttached){
				clickHandlerAttached = true;
				$('.group, #monitor-group').click(function(e){
					var group = $(e.target).parents('.group');
					onGroupClick(group);
				});
				$("body").css("cursor", "default");
				$('#waitBackground').hide();
			}
		});
	}

	$("body").css("cursor", "progress");
	$('#waitBackground').show();
	$('.widget-body').css('overflow', 'hidden');
	var stateQueries = [];
	$('.group').each(function(index) {
		var q = renderState($(this));
		if(q != null && q)
			stateQueries.push(q);
	});

	function waitQueries(index){
		var callback = function(){
			if(index < stateQueries.length){
				waitQueries(index + 1);
			}else{
				getDataItem(data[0], 0);
			}
		};
		$.when(stateQueries[index]).then(
			callback,
			callback
		);
	}
	waitQueries(0);
};
