#************************************************************************* # $ScriptName: "GetOpsMgrDBPercentFreeSpace" $ # # Purpose: This script gets Operations Manager DB Percentage of free space. It returns the exact same value as of SQL MP's "MSSQL on Windows: DB Free Space Total (%)" rule # # $File: GetOpsMgrDBPercentFreeSpace.ps1 $ # # Parameters that should be passed to this script # 0 DatabaseServerName # 1 DatabaseName
<# -- The 2 GRANTs below are required on target SQL instance: use master GRANT VIEW SERVER STATE TO [YOURDOMAIN\SCOM_db_datawriter] GRANT VIEW ANY DEFINITION TO [YOURDOMAIN\SCOM_db_datawriter] #> #*************************************************************************
param($DatabaseServerName, $DatabaseName)
function CalculateGroupFreeSpace($files) { $diskArray = @{}
[double]$groupAvailableSpaceMB = 0.0 [double]$groupUsedSpaceMB = 0 [double]$groupAvailableSpaceNoGrowthMB = 0 [double]$groupUsedSpaceNoGrowthMB = 0 [bool]$noUsedSpaceInfo = $false
[bool]$isTotalAutoGrow = $false
if ($files -ne $null) { foreach ($file in $files) { $noUsedSpaceInfo = $noUsedSpaceInfo -or ($file.FileUsedSpaceMB -eq -1) $isTotalAutoGrow = $isTotalAutoGrow -or $file.IsAutoGrowEnabled
$groupAvailableSpaceNoGrowthMB = $groupAvailableSpaceNoGrowthMB + $file.FileSizeMB $groupUsedSpaceNoGrowthMB = $groupUsedSpaceNoGrowthMB + $file.FileUsedSpaceMB $groupAvailableSpaceMB = $groupAvailableSpaceMB + $file.FileSizeMB $groupUsedSpaceMB = $groupUsedSpaceMB + $file.FileUsedSpaceMB
$fileSize = $file.FileSizeMB
if ($file.IsAutoGrowEnabled) { [double]$growthSpaceDisk = 0
$growthSpaceMaxSize = [double]::MaxValue
$fileGrowth = [double]$file.FileGrowth $fileMaxSize = $file.FileMaxSizeMB
if ($file.IsPercentGrowth) { $fileGrowth = $fileGrowth / 100.0
# // when the FileSize approaches the limits of the logical disk, a large FileGrowth # // value can cause an error if the file tries to grow past the physical limit # // calculate actual growth capacity on disk
[int]$numberOfGrowths = CastToInt ([Math]::Log(($file.DiskFreeSpaceMB + $fileSize) / $fileSize) / [Math]::Log(1 + $fileGrowth)) $growthSpaceDisk = $fileSize * [Math]::Pow(1.0 + $fileGrowth,$numberOfGrowths) - $fileSize
if ( (CastToInt $fileMaxSize) -ne -1) # //FileMaxSize Not Unlimited { # // when the FileSize approaches the MaxSize, a large FileGrowth value can cause an error # // if the file tries to grow past the MaxSize value. # // calculate actual growth capacity
$numberOfGrowths = CastToInt ([Math]::Log($fileMaxSize / $fileSize)/ [Math]::Log(1 + $fileGrowth)) $growthSpaceMaxSize = $fileSize * [Math]::Pow(1 + $fileGrowth,$numberOfGrowths) - $fileSize } } else { $fileGrowth = $fileGrowth / 128.0
$growthSpaceDisk = (CastToInt ($file.DiskFreeSpaceMB / $fileGrowth)) * $fileGrowth
if ( (CastToInt $fileMaxSize) -ne -1) { $growthSpaceMaxSize = ((CastToInt ($fileMaxSize - $fileSize)) / $fileGrowth) * $fileGrowth } }
$growthAllowedMB = if ($growthSpaceMaxSize -lt $growthSpaceDisk) {$growthSpaceMaxSize} else {$growthSpaceDisk}
[string]$storageKey = GetStorageKey $file.PhysicalName $file.VolumeMountPoint $file.VolumeId
[double]$logicalDriveGrowth = 0
$logicalDriveGrowth = $diskArray[$storageKey] if ($logicalDriveGrowth -ne $null) { $tempSize = $logicalDriveGrowth + $growthAllowedMB
# // Using of DiskFreeSpaceMB may lead to incorrect results in the case # // when several files are on the same disk and have large file growth # // TODO: Think about whether we can use something better then DiskFreeSpaceMB in this case $diskArray[$storageKey] = if ($tempSize -gt $file.DiskFreeSpaceMB) {$file.DiskFreeSpaceMB} else {$tempSize} } else { $tmp_growthAllowedMB = if ($growthAllowedMB -gt $file.DiskFreeSpaceMB) {$file.DiskFreeSpaceMB} else {$growthAllowedMB} $diskArray.Add($storageKey, $tmp_growthAllowedMB ) } } } }
foreach ($storageGrowth in $diskArray.Values) { $groupAvailableSpaceMB += $storageGrowth }
$result = (New-Object -TypeName psobject)
$result | Add-Member -MemberType NoteProperty -Name NoSizeAllocationInfo -Value $noUsedSpaceInfo $result | Add-Member -MemberType NoteProperty -Name UsedSpaceMB -Value $groupUsedSpaceMB $result | Add-Member -MemberType NoteProperty -Name FreeSpaceMB -Value ($groupAvailableSpaceMB - $groupUsedSpaceMB)
$result | Add-Member -MemberType NoteProperty -Name FreeSpacePercent -Value 0 if ($groupAvailableSpaceMB -ne 0.0 -and $result.FreeSpaceMB -gt 0) { $result.FreeSpacePercent = $result.FreeSpaceMB / $groupAvailableSpaceMB * 100.0 } else { $result.FreeSpaceMB = 0.0 $result.FreeSpacePercent = 0.0 }
$result | Add-Member -MemberType NoteProperty -Name SizeMB -Value $groupAvailableSpaceNoGrowthMB $result | Add-Member -MemberType NoteProperty -Name FreeSpaceAutoGrowMB -Value ($groupAvailableSpaceNoGrowthMB - $groupUsedSpaceNoGrowthMB)
$result | Add-Member -MemberType NoteProperty -Name FreeSpaceAutoGrowPercent -Value 0 if ($groupAvailableSpaceNoGrowthMB -ne 0.0 -and $result.FreeSpaceAutoGrowMB -gt 0.0) { $result.FreeSpaceAutoGrowPercent = $result.FreeSpaceAutoGrowMB / $groupAvailableSpaceNoGrowthMB * 100.0 } else { $result.FreeSpaceAutoGrowMB = 0.0 $result.FreeSpaceAutoGrowPercent = 0.0 }
$result | Add-Member -MemberType NoteProperty -Name AllocatedFreeSpaceMB -Value ($result.SizeMB - $result.UsedSpaceMB) $result.AllocatedFreeSpaceMB = if($result.AllocatedFreeSpaceMB -lt 0.0) { 0.0 } else { $result.AllocatedFreeSpaceMB}
$result | Add-Member -MemberType NoteProperty -Name DiskFreeSpaceMB -Value ($result.FreeSpaceMB - $result.AllocatedFreeSpaceMB) $result.DiskFreeSpaceMB = if($result.DiskFreeSpaceMB -lt 0.0) { 0.0 } else { $result.DiskFreeSpaceMB};
$result | Add-Member -MemberType NoteProperty -Name TotalSpaceMB -Value ($result.DiskFreeSpaceMB + $result.AllocatedFreeSpaceMB + $result.UsedSpaceMB)
$result | Add-Member -MemberType NoteProperty -Name AllocatedFreeSpacePercent -Value 0 $result | Add-Member -MemberType NoteProperty -Name DiskFreeSpacePercent -Value 0 $result | Add-Member -MemberType NoteProperty -Name UsedSpacePercent -Value 0 if ($result.TotalSpaceMB -ne 0.0) { $result.AllocatedFreeSpacePercent = $result.AllocatedFreeSpaceMB / $result.TotalSpaceMB * 100.0 $result.DiskFreeSpacePercent = $result.DiskFreeSpaceMB / $result.TotalSpaceMB * 100.0 $result.UsedSpacePercent = $result.UsedSpaceMB / $result.TotalSpaceMB * 100.0 } else { $result.AllocatedFreeSpacePercent = 0.0 $result.DiskFreeSpacePercent = 0.0 $result.UsedSpacePercent = 0.0 }
return $result }
function GetStorageKey([string]$physicalName, [string]$volumeMountPoint, [string]$volumeId) { [System.Text.RegularExpressions.Regex]$AzureRegex = (New-Object -TypeName System.Text.RegularExpressions.Regex -ArgumentList '^https?://([\da-z\.-]+)\.(?:[a-z\.]{2,6})(?:[/\w \.-]*)*/?$', ([System.Text.RegularExpressions.RegexOptions]::Compiled -bor [System.Text.RegularExpressions.RegexOptions]::CultureInvariant -bor [System.Text.RegularExpressions.RegexOptions]::IgnoreCase)) [System.Text.RegularExpressions.Regex]$LocalStorageRegex = (New-Object -TypeName System.Text.RegularExpressions.Regex -ArgumentList '^(?:(?:\\\\\?\\)?(?<drive>(?:[A-Z]\:))|(?:\\\\\?\\(?<drive>Volume\{[a-f0-9]{8}(?:-[a-f0-9]{4}){3}-[a-f0-9]{12}\})))(?<relpath>(?:[\\/][^\\\:/]+)+(?:\:[^\\\:/]+)?)[\\/]?$', ([System.Text.RegularExpressions.RegexOptions]::Compiled -bor [System.Text.RegularExpressions.RegexOptions]::CultureInvariant -bor [System.Text.RegularExpressions.RegexOptions]::IgnoreCase)) [System.Text.RegularExpressions.Regex]$RemoteStorageRegex = (New-Object -TypeName System.Text.RegularExpressions.Regex -ArgumentList '^\\\\(?:\?\\(?:UNC\\)?)?(?<host>(?!(?:Volume\{[a-f0-9]{8}(?:-[a-f0-9]{4}){3}-[a-f0-9]{12}\})|\?)[^\\\/:]+)[\\/](?<relpath>[^\\\/:]+(?:[\\/][^\\\:]+)*(?:\:[^\\\/:]+)?)[\\/]?$', ([System.Text.RegularExpressions.RegexOptions]::Compiled -bor [System.Text.RegularExpressions.RegexOptions]::CultureInvariant -bor [System.Text.RegularExpressions.RegexOptions]::IgnoreCase))
$match = $AzureRegex.Match($physicalName)
if ($match.Success) { return $match.Value }
if (-not [string]::IsNullOrEmpty($volumeId)) { return $volumeId }
$correctedPath = $physicalName.Replace('/','\')
$match = $RemoteStorageRegex.Match($correctedPath)
if ($match.Success) { $subMatch = $RemoteStorageRegex.Match($volumeMountPoint) $hostsrv = $subMatch.Groups["host"].Captures[0].Value
# // Not only Windows can hold SMB share. # // SMB share's path on Linux can be either case sensitive or insensitive (configurable) # // We don't know which OS holds the target share. # // That's why we put to upper case only host name
$storageKey = [string]::Format('\\{0}\{1}',$hostsrv.ToUpper(),$subMatch.Groups["relpath"].Captures[0].Value)
return $storageKey }
throw (New-Object -TypeName Exception -ArgumentList "Unsupported path format") }
function GetMonitoringData([string]$commandText, [string]$_azureMaxFileSizeMB, [string]$databaseServerName, [string]$databaseName) { # . .\GetSqlNativeClientCommon.ps1 # this should be included by the ModuleImplementation $sqlAuth = GetSqlAuthentication
$cn = New-Object -TypeName System.Data.SqlClient.SqlConnection $cn.ConnectionString ="Server=" + $databaseServerName + ";" + $sqlAuth + ";Initial Catalog=" + $databaseName
$adp = New-Object -TypeName System.Data.SqlClient.SqlDataAdapter -ArgumentList $commandText,$cn $azureMaxFileSizeMBPar = (New-Object -TypeName System.Data.SqlClient.SqlParameter -ArgumentList "@azureMaxFileSizeMB",$_azureMaxFileSizeMB) $adp.SelectCommand.Parameters.Add($azureMaxFileSizeMBPar) | Out-Null
$dt = New-Object -TypeName System.Data.DataTable $adp.Fill($dt) | out-null
$files = @() foreach ($row in $dt.Rows) { if ([string]::IsNullOrEmpty($databaseName) -eq $false -and $row["DatabaseName"].ToString() -ne $databaseName) { continue } # we are only interested with the given $databaseName if ($row["FileType"] -ne 1) { continue } # we are only interested with ROWS Data
$dbData = (New-Object -TypeName psobject)
$dbData | Add-Member -MemberType NoteProperty -Name PhysicalName -Value $row["PhysicalName"] $dbData | Add-Member -MemberType NoteProperty -Name FileSizeMB -Value $row["FileSizeMB"] $dbData | Add-Member -MemberType NoteProperty -Name FileUsedSpaceMB -Value $row["FileUsedSpaceMB"] $dbData | Add-Member -MemberType NoteProperty -Name FileMaxSizeMB -Value $row["FileMaxSizeMB"] $dbData | Add-Member -MemberType NoteProperty -Name IsAutoGrowEnabled -Value $row["IsAutoGrowEnabled"] $dbData | Add-Member -MemberType NoteProperty -Name IsPercentGrowth -Value $row["IsPercentGrowth"] $dbData | Add-Member -MemberType NoteProperty -Name FileGrowth -Value $row["FileGrowth"] $dbData | Add-Member -MemberType NoteProperty -Name Size -Value $row["Size"] $dbData | Add-Member -MemberType NoteProperty -Name VolumeMountPoint -Value $row["VolumeMountPoint"] $dbData | Add-Member -MemberType NoteProperty -Name VolumeId -Value $row["VolumeId"] $dbData | Add-Member -MemberType NoteProperty -Name DiskFreeSpaceMB -Value $row["DiskFreeSpaceMB"]
$files += $dbData }
$rowsCalcData = CalculateGroupFreeSpace $files
return [System.Math]::Round( $rowsCalcData.FreeSpacePercent, 2 ) }
function CastToInt($valueToCastToInt) { # original code in SQL MP is using (int) casting in C# however [int] casting in PS returns different results. # E.g. (int)3.5 in C# returns 3 , while [int]3.5 in PS returns 4 ! # In fact, C# simply truncates, even with negative values. # Therefore we directly use Truncate() here in PS.
[Math]::Truncate($valueToCastToInt) }
function GetSqlDriverName() { # preparing reg drive. It will be removed automatically after exiting from this function. if (-not (Get-PSDrive -PSProvider Registry -Name HKCR -ErrorAction SilentlyContinue)) { New-PSDrive -PSProvider Registry -Root HKEY_CLASSES_ROOT -Name HKCR | Out-Null }
# checking if MSOLEDBSQL is installed if (Get-ChildItem -Path HKCR:\MSOLEDBSQL -Include Clsid -Recurse -ErrorAction SilentlyContinue) { if ( (Get-ItemProperty -Path HKCR:\MSOLEDBSQL\Clsid).'(default)'.ToUpper() -eq '{5A23DE84-1D7B-4A16-8DED-B29C09CB648D}' ) { return "MSOLEDBSQL" } }
# MSOLEDBSQL not found, checking for "SQL Server Native Client" $sqlncli_Key = Get-ChildItem -Path "HKLM:\SOFTWARE\ODBC\ODBCINST.INI" -Include "SQL Server Native Client*" -Recurse | Select-Object -First 1 #getting the first one in case there is an RDA Client as well if ($sqlncli_Key) { $driverFileFullPath = $sqlncli_Key.GetValue("Driver") if ($driverFileFullPath) { return [System.IO.Path]::GetFileNameWithoutExtension($driverFileFullPath) # returns usually "sqlncli11" } }
# SQL Server Native Client not found, directly return SQLOLEDB return "SQLOLEDB" }
function GetSqlAuthentication() { $Aquila_Key = Get-ItemProperty -Path HKLM:\SOFTWARE\Microsoft\Aquila -ErrorAction SilentlyContinue if ($Aquila_Key) { if ( $Aquila_Key.'SQLAuth' -eq 'AADI' ) { return "Authentication=ActiveDirectoryIntegrated" } }
return "Integrated Security=SSPI" }
$sql_GetDBSpaceData = @' -- The 2 GRANTs below are required on target SQL instance: --use master --GRANT VIEW SERVER STATE TO [YOURDOMAIN\SCOM_db_datawriter] --GRANT VIEW ANY DEFINITION TO [YOURDOMAIN\SCOM_db_datawriter]
DECLARE @dbName sysname, @sql nvarchar(2000), @needsMaster BIT;
IF OBJECT_ID ('tempdb.dbo.#ResultTable') IS NOT NULL DROP TABLE #ResultTable; CREATE TABLE #ResultTable ( [DatabaseName] SYSNAME ,[FileName] SYSNAME ,[FileType] TINYINT ,FileSizeMB FLOAT(53) ,FileUsedSpaceMB FLOAT(53) ,FileMaxSizeMB FLOAT(53) ,IsAutoGrowEnabled BIT ,IsPercentGrowth BIT ,FileGrowth INT ,VolumeMountPoint NVARCHAR(256) ,VolumeId NVARCHAR(256) ,GroupKey NVARCHAR(22) ,FilegroupName SYSNAME ,FilegroupType NVARCHAR(2) ,DiskFreeSpaceMB FLOAT(53) ,PhysicalName NVARCHAR(260) );
DECLARE fileCursor CURSOR LOCAL STATIC FOR SELECT [name] ,CAST(CASE WHEN d.[replica_id] IS NOT NULL OR d.is_in_standby = 1 THEN 1 ELSE 0 END AS BIT) AS [needsMaster] FROM sys.databases d WHERE HAS_DBACCESS([name]) = 1;
OPEN fileCursor; FETCH NEXT FROM fileCursor INTO @dbName, @needsMaster;
WHILE @@Fetch_Status=0 BEGIN SET @sql = N'USE '+ QUOTENAME(@dbName ,'"') + N'; SELECT DB_NAME() AS [DatabaseName] ,N'''' AS [FileName] ,CASE WHEN f.[type] = 1 THEN 0 WHEN fg.[type] COLLATE DATABASE_DEFAULT = ''FG'' THEN 1 WHEN fg.[type] COLLATE DATABASE_DEFAULT = ''FD'' THEN 2 WHEN fg.[type] COLLATE DATABASE_DEFAULT = ''FX'' THEN 3 END AS [FileType] ,CONVERT(FLOAT(53), f.size / 128.0) AS [FileSizeMB] ,CONVERT(FLOAT(53), CASE WHEN f.[type] IN (0,1) THEN FILEPROPERTY(f.name, ''SpaceUsed'') / 128.0 ELSE -1 END) AS [FileUsedSpaceMB] ,CONVERT(FLOAT(53), CASE WHEN f.max_size = -1 OR f.max_size = 268435456 THEN -1 ELSE f.max_size / 128.0 END) as [FileMaxSizeMB] ,CASE WHEN f.growth = 0 THEN 0 ELSE 1 END AS IsAutoGrowEnabled ,f.is_percent_growth as [IsPercentGrowth] ,f.growth AS [FileGrowth] ,ovs.volume_mount_point AS [VolumeMountPoint] ,ovs.volume_id AS [VolumeId] ,N'''' AS [GroupKey] ,N'''' AS [FilegroupName] ,N'''' AS [FilegroupType]';
IF @needsMaster = 0 BEGIN SET @sql = @sql + N' ,CONVERT(FLOAT(53), CASE WHEN CharIndex(N''https://'',LTRIM(f.physical_name)) = 1 OR CharIndex(N''http://'',LTRIM(f.physical_name)) = 1 THEN @azureMaxFileSizeMB - f.size / 128.0 ELSE ovs.available_bytes / 1048576.0 END) AS [DiskFreeSpaceMB] ,f.physical_name AS [PhysicalName] FROM sys.database_files AS f WITH (NOLOCK)'; END; ELSE BEGIN SET @sql = @sql +N' ,CONVERT(FLOAT(53), CASE WHEN CharIndex(N''https://'',LTRIM(mf.physical_name)) = 1 OR CharIndex(N''http://'',LTRIM(mf.physical_name)) = 1 THEN @azureMaxFileSizeMB - f.size / 128.0 ELSE ovs.available_bytes / 1048576.0 END) AS [DiskFreeSpaceMB] ,mf.physical_name AS [PhysicalName] FROM sys.database_files AS f WITH (NOLOCK) JOIN sys.master_files AS mf WITH (NOLOCK) ON f.file_id = mf.file_id AND mf.database_id = DB_ID()'; END;
SET @sql = @sql + N' LEFT JOIN sys.filegroups fg WITH (NOLOCK) ON f.data_space_id = fg.data_space_id CROSS APPLY sys.dm_os_volume_stats(DB_ID(), f.file_id) ovs WHERE f.is_read_only = 0 AND ((f.[type] = 1 AND fg.data_space_id IS NULL) OR (f.[type] IN (0,2) AND fg.data_space_id IS NOT NULL))';
--BEGIN TRY INSERT INTO #ResultTable EXEC sp_executesql @sql, N'@azureMaxFileSizeMB INT', @azureMaxFileSizeMB = @azureMaxFileSizeMB --END TRY --BEGIN CATCH --END CATCH; FETCH NEXT FROM fileCursor INTO @dbName, @needsMaster; END
CLOSE fileCursor; DEALLOCATE fileCursor;
SELECT * FROM #ResultTable '@
# ....................................................................................
#Create PropertyBag object $momAPI = New-Object -ComObject MOM.ScriptAPI $oBag = $momAPI.CreatePropertyBag()
#Define local event constants $EVENT_TYPE_ERROR = 1 $EVENT_TYPE_WARNING = 2 $EVENT_TYPE_INFORMATION = 4
$error.Clear() $resultToReturn = 0
try { [string]$_azureMaxFileSizeMB = 1048576 $resultToReturn = GetMonitoringData $sql_GetDBSpaceData $_azureMaxFileSizeMB $databaseServerName $databaseName
$oBag.AddValue("ErrorFlag", 0) $oBag.AddValue("Error", "No error occurred.") } catch { $strMessage = ($_ | fl) | Out-String $momAPI.LogScriptEvent( "GetOpsMgrDBPercentFreeSpace.ps1", 100, $EVENT_TYPE_WARNING, $strMessage )
$oBag.AddValue("ErrorFlag", 1) $oBag.AddValue("Error", $strMessage) }
$oBag.AddValue("DBPercentFreeSpace", $resultToReturn) $oBag

